import React,{useState} from 'react'
import axios from 'axios'

function AddUser() {
    const [title,setTitle]= useState("");
    const [content,setContent]= useState("");

    const saveNote = async (e) =>{
        e.preventDefault();
        try {
            await axios.post('http://localhost:5000/notes',{
                title,
                content
            });
            navigate("/");
        } catch (error) {
            console.log(error);
        }
    }

  return (
    <div className="columns mt-5 is-centered">
        <div className="column is-half">
            <form onSubmit={saveNote}>
                <div className="field">
                    <label className="label">Title</label>
                    <div className="control">
                        <input type="text" className="input" value={title} onChange={(e)=>setTitle(e.target.value)} placeholder='Title of your note' />
                    </div>
                </div>
                <div className="field">
                    <label className="label">Content</label>
                    <div className="control">
                        <input type="textarea" className="input" value={title} onChange={(e)=>setContent(e.target.value)} placeholder='Content of your note' />
                    </div>
                </div>
                <div className="field">
                    <button type='submit' className='button is-success'>Save</button>
                </div>
            </form>
        </div>
    </div>
  )
}

export default AddUser
