import React, {useState, useEffect} from 'react';
import axios from "axios";
import { Link,useNavigate,useParams } from 'react-router-dom';

function DetailNote() {
    const [notes, setNotes] = useState({});
    const navigate = useNavigate();
    const {id} = useParams();

    useEffect(() =>{
        getNoteById();
    }, []);

    const getNoteById = async () =>{
        const response = await axios.get(`http://localhost:5000/notes/${id}`);
        setNotes(response.data);
    }

    const deleteNote = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/notes/${id}`);
            navigate("/")
        } catch (error) {
            console.log(error);
        }
    }

  return (
    <div className="columns mt-5 is-centered">
        <div className="column is-half">
            <div className="title">
            {notes.title}
            </div>
            <Link to={`edit/${notes.id}`} className='button is-info is-small'>Edit</Link>
            <button onClick={()=> deleteNote(notes.id)} className='button is-danger is-small'>Delete</button>
            <table className='table is-fullwidth'>
                <thead>
                    <tr>
                        <th>{notes.content}</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>
                            <Link to={'NoteList'} className='button is-info is-fullwidth'>OK</Link>
                        </td>
                    </tr>
                </tbody>
            </table>
        </div>
    </div>
  )
}

export default DetailNote
